insert into application.stories (name, attributes) values ( 'worlds-largest-orgy', '{"url":"https://lasvegasweekly.com/intersection/2018/may/03/worlds-biggest-orgy-to-be-attempted-in-las-vegas/?utm_source=fark&utm_medium=website&utm_content=link&ICID=ref_fark","date":"Thu May 10 2018","title":"WORLD’S BIGGEST ORGY TO BE ATTEMPTED IN LAS VEGAS","image":"assets/thumbs/orgy.jpg","excerpt":"The five-day festival devoted to carnal pleasure and topless camaraderie will include workshops (examples: Sensual Bondage Basics, Laughter Belongs in the Dungeon and Intro to Lifestyle/Consent 101), sex toy demos, naughty parties (the weekend kickoff party is called Foreplay, while the Bunny Style pool party serves as a tribute to Hugh Hefner) and, surely serving as the weekend climax, the attempt to break the record for the world’s largest orgy.","submitter":"sanjeev"}'::jsonb );
insert into application.stories (name, attributes) values ( 'man-moves-UPS-HQ-to-his-home', '{"url":"https://www.npr.org/sections/thetwo-way/2018/05/10/610102872/man-allegedly-used-change-of-address-form-to-move-ups-headquarters-to-his-apartm","date":"Fri May 11 2018","title":"Man Moves UPS Headquarters To His Apartment","image":"assets/thumbs/ups.jpg","excerpt":"Dushaun Henderson-Spruce reportedly submitted a U.S. Postal Service change of address form on Oct. 26, 2017 for the UPS corporation, according to court documents. He requested changing a corporation''s mailing address from an address in Atlanta to the address of his apartment on Chicago''s North Side. The post office duly updated the address, and Henderson-Spruce allegedly began receiving the company''s mail — including checks. Prosecutors say he deposited some $58,000 in checks improperly forwarded to his address.","submitter":"kavi"}'::jsonb );
insert into application.stories (name, attributes) values ( 'rocks-make-sea-level-rise', '{"url":"http://www.sciencemag.org/news/2018/05/republican-lawmaker-rocks-tumbling-ocean-causing-sea-level-rise","date":"Sat May 19 2018","title":"Republican lawmaker: Rocks tumbling into ocean causing sea level rise","image":"assets/thumbs/ostrich-head-in-sand.jpg","excerpt":"The Earth is not warming. The White Cliffs of Dover are tumbling into the sea and causing sea levels to rise. Global warming is helping grow the Antarctic ice sheet. Those are some of the skeptical assertions echoed by Republicans on the U.S. House of Representatives Science, Space and Technology Committee yesterday.","submitter":"vasu"}'::jsonb );
insert into application.stories (name, attributes) values ( 'police-called-on-arnab-debate-noise', '{"url":"http://bengalurupost.com/hearing-loud-noise-neighbors-call-cops-suspecting-domestic-violence-later-find-family-watching-arnabs-debate/","date":"Sun May 20 2018","title":"Hearing Loud Noise Neighbors Call Cops Suspecting Domestic Violence, Find Family Watching Arnab’s Debate","image":"assets/thumbs/loud-arnab.jpg","excerpt":"Bengaluru: Prakash and Richa who recently returned from UK called Police helpline after they heard loud noise from their neighbor’s house. Much to the embarrassment of Prakash and Richa, Police patrolling team after knocking on the neighbor’s door found the family happily enjoying when republic TV’s flagship program ‘The Debate with Arnab Goswami’ was running in the background.","submitter":"easundaram"}'::jsonb );
insert into application.stories (name, attributes) values ( 'vishnu-dont-come-to-office', '{"url":"https://www.deccanchronicle.com/nation/current-affairs/180518/gujarat-government-officer-i-am-kalki-avatar-cant-come-to-office.html","date":"Sun May 20 2018","title":"I’m Kalki, Lord Vishnu’s 10th avatar, can’t come to office: Gujarat govt officer","image":"assets/thumbs/vishnu-avatar-kalki.jpeg","excerpt":"A Gujarat government official has claimed that he is Kalki, the tenth incarnation of Vishnu, and can''t come to office because he is conducting a \"penance\" to \"change the global conscience\". Thanks to his penance, the country is getting good rains, said Rameshchandra Fefar, superintending engineer with the Sardar Sarovar Punarvasvat Agency (SSPA), in reply to a show-cause notice served to him.","submitter":"vmk"}'::jsonb );
